import React, { useContext, useState } from 'react'
import {Formik, Form, Field, ErrorMessage} from "formik"
import * as Yup from 'yup'
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { AuthContext } from '../helpers/AuthContext';

function AddImage() {
    const {authState} = useContext(AuthContext);
    const [file, setFile] = useState([]);
    let navigate = useNavigate();

    const initialValues = {
        title: "",
        bytecode: {},
        username: authState.username,
        description: ""
    };

    const validationSchema = Yup.object().shape({
        title: Yup.string().required(),
        bytecode: Yup.array().min(1,"select at least 1 file"),
        username: Yup.string().min(3).max(15).required(),
        description: Yup.string().required()

    });

    const onSubmit = (data) => {
        window.alert(JSON.stringify(data));
        data.file = new FormData().append('file', data.file);
        axios.post("http://localhost:3001/images", data, {
            headers: {
                "Content-Type": "multipart/form-data",
              }
        }).then((response) => {
          console.log(response);
        })
        navigate("/");
    }

    if(!authState.status){
        navigate("/login")
    }

    return (
        <div className="addImagePage">
            <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>
                <Form className="formContainer" >
                    <label>Title:</label>
                    <ErrorMessage name="title" component="span"/>
                    <Field id="inputAddImage" name="title" placeholder="Title"/>
                    <label>Title:</label>
                    <ErrorMessage name="description" component="span"/>
                    <Field id="inputAddImage" name="description" placeholder="Description"/>
                    <label>Image:</label>
                    <input type="file" name="bytecode" onChange={(event) => {
                        formik.setFieldValue("bytecode", event.target.files);
                    }}/>
                    <button type="submit" disabled={formik.isSubmitting}>Add Image</button>
                </Form>
            </Formik>
        </div>
    );
}

export default AddImage
