import React, {useContext, useState} from 'react'
import {Formik, Form, Field, ErrorMessage} from "formik"
import * as Yup from 'yup'
import axios from "axios";
import { AuthContext } from '../helpers/AuthContext';
import { useNavigate } from "react-router-dom";

function AddImage() {
    const {authState} = useContext(AuthContext);
    // const [file, setFile] = useState([]);
    let navigate = useNavigate();

    const initialValues = {
        title: "",
        bytecode: [],
        username: authState.username,
        description: ""
    };

    const validationSchema = Yup.object().shape({
        title: Yup.string().required(),
        bytecode: Yup.array().min(1,"select at least 1 file"),
        description: Yup.string().required()

    });

    const onSubmit = (data) => {
        console.log(data)
        axios.post("http://localhost:3001/images", data, {
            headers: {
              "Content-Type": "multipart/form-data",
            }
        })
        .then((response) => {
          console.log("New image submitted");
        })
    }

    if(!authState.status){
        navigate("/login")
    }    return (
        <div className="addImagePage">
            <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>
                {(formik) => {
                    return (
                        <>
                            <Form className="formContainer" >
                                <label>Title:</label>
                                <ErrorMessage name="title" component="span"/>
                                <Field id="inputAddImage" name="title" placeholder="Title"/>
                                <label>Title:</label>
                                <ErrorMessage name="description" component="span"/>
                                <Field id="inputAddImage" name="description" placeholder="Description"/>
                                <label>Image:</label>
                                <ErrorMessage name="bytecode" component="span"/>
                                <input
                                    id="file"
                                    name="bytecode"
                                    type="file"
                                    onChange={ async (event) => {
                                        formik.setFieldValue("bytecode", await event.target.files[0].text());
                                    }}
                                />
                                <button type="submit">Add Image</button>
                            </Form>
                        </>
                    )
                }}
            </Formik>
        </div>
    );
}

export default AddImage
