import React, { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { AuthContext } from '../helpers/AuthContext';
import { useNavigate } from "react-router-dom";

function Image() {
  let { id } = useParams();
  const [imageObject, setImageObject] = useState({});
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const {authState} = useContext(AuthContext);
  let navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:3001/images/byId/${id}`).then((response) => {
      setImageObject(response.data);
    });

    axios.get(`http://localhost:3001/comments/${id}`).then((response) => {
      setComments(response.data);
    });
    // eslint-disable-next-line
  }, []);

  const addComment = () => {
    axios
      .post("http://localhost:3001/comments", {
        commentBody: newComment,
        ImageId: id,
      },
      {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        }
      })
      .then((response) => {
        if (response.data.error){
          alert(response.data.error)
        }
        else {
          const commentToAdd = response.data;
          setComments([...comments, commentToAdd]);
          setNewComment("");
        }
      });
  };

  const deleteComment = (id) => {
    axios.delete(`http://localhost:3001/comments/${id}`, {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      }
    })
    .then(() => {
      setComments(comments.filter((comment) => {
        return comment.id !== id;
      }))
    })
  }

  const blobToImage = async (blob) => {
     const promise = new Promise(resolve => {
      const url = URL.createObjectURL(blob)
      let img = new Image()
      img.onload = () => {
        URL.revokeObjectURL(url)
        resolve(img)
      }
      img.src = url
    })

    return await promise
  }

  if(!authState.status){
    navigate("/login")
}

  return (
    <div className="imagePage">
      <div className="leftSide">
        <div className="image" id="individual">
          <div className="title">{imageObject.title}</div>
          <div className="body">{blobToImage(imageObject.bytecode)}</div>
          <div className="username">Author: {imageObject.username}</div>
          <div className="description">{imageObject.description}</div>
        </div>
      </div>
      <div className="rightSide">
        <div className="addCommentContainer">
          <input
            type="text"
            placeholder="Comment..."
            value={newComment}
            onChange={(event) => {
              setNewComment(event.target.value);
            }}
          />
          <button onClick={addComment}> Add Comment</button>
        </div>
        <div className="listOfComments">
          {comments.map((comment, key) => {
            return (
              <div key={key} className="comment">
                {comment.commentBody}
                <label> Username: {comment.username}</label>
                { authState.username === comment.username && <button onClick={() => { deleteComment(comment.id) }}> Delete </button> }
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default Image;
