import React, { useContext } from 'react'
import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from '../helpers/AuthContext';

function Home() {

    const {authState} = useContext(AuthContext);
    const [listOfImages, setListOfImages] = useState([])
    let navigate = useNavigate();

     useEffect(() => {
        axios.get("http://localhost:3001/images").then((response) => {
          setListOfImages(response.data);
        })
     }, []);

    const blobToImage = (value) => {
        const file = new File(Array.from(value.bytecode), `Image${value.id}.png`, {
            type: 'image/png'
        })

        var reader  = new FileReader();
        // it's onload event and you forgot (parameters)
        reader.onload = function(e)  {
            var image = document.createElement("img");
            // the result image data
            image.src = e.target.result;
            document.body.appendChild(image);
        }
        // you have to declare the file loading
        reader.readAsDataURL(file);
    }

    if(!authState.status){
        navigate("/login")
    }

    return (
        <div>
            {listOfImages.map((value, key) => {
                return (
                <div 
                    key={key}
                    className="image" 
                    onClick={() => {
                        navigate(`/image/${value.id}`);
                    }}>
                        <div className='title'>{value.title}</div>
                        <div className='body'>{blobToImage(value)}</div>
                        <div className='username'>Author: {value.username}</div>
                        <div className='description'>{value.description}</div>
                </div>
                );
            })}
        </div>
    )
}

export default Home
