import React from 'react'
import {Formik, Form, Field, ErrorMessage} from "formik"
import * as Yup from 'yup'
import axios from "axios";
import { useEffect, useState } from "react";

function AddImage() {
    const initialValues = {
        title: "",
        bytecode: "",
        username: "",
        description: ""
    };

    const validationSchema = Yup.object().shape({
        title: Yup.string().required(),
        bytecode: Yup.string().required(),
        username: Yup.string().required(),
        description: Yup.string().required()

    });

    const onSubmit = (data) => {
        axios.post("http://localhost:3001/images", data).then((response) => {
          setListOfImages(response.data);
        })
    }

    return (
        <div className="addImagePage">
            <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>
                <Form className="formContainer" >
                    <label>Title:</label>
                    <ErrorMessage name="title" component="span"/>
                    <Field id="inputAddImage" name="title" placeholder="Title"/>
                    <label>Image:</label>
                    <ErrorMessage name="bytecode" component="span"/>
                    <Field id="inputAddImage" name="bytecode" placeholder="Title"/>
                    <label>Username:</label>
                    <ErrorMessage name="username" component="span"/>
                    <Field id="inputAddImage" name="username" placeholder="Username"/>
                    <label>Title:</label>
                    <ErrorMessage name="description" component="span"/>
                    <Field id="inputAddImage" name="description" placeholder="Description"/>
                    <button type="submit">Add Image</button>
                </Form>
            </Formik>
        </div>
    );
}

export default AddImage
