import React, { useContext } from 'react'
import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from '../helpers/AuthContext';

function Home() {

    const {authState} = useContext(AuthContext);
    const [listOfImages, setListOfImages] = useState([])
    let navigate = useNavigate();

     useEffect(() => {
        axios.get("http://localhost:3001/images").then((response) => {
          setListOfImages(response.data);
        })
     }, []);

    const blobToImage = (blob) => {
        new Promise(resolve => {
          const url = URL.createObjectURL(new Blob(blob, {
            type: 'text/plain'
          }))
          let img = new Image()
          img.onload = () => {
            URL.revokeObjectURL(url)
            resolve(img)
          }
          img.src = url
        })
        .then((value) => {
            return value;
        })
    }

    if(!authState.status){
        navigate("/login")
    }

    return (
        <div>
            {listOfImages.map((value, key) => {
                return (
                <div 
                    key={key}
                    className="image" 
                    onClick={() => {
                        navigate(`/image/${value.id}`);
                    }}>
                        <div className='title'>{value.title}</div>
                        <div className='body'>{blobToImage(value.bytecode)}</div>
                        <div className='username'>Author: {value.username}</div>
                        <div className='description'>{value.description}</div>
                </div>
                );
            })}
        </div>
    )
}

export default Home
